    const route = "nav";

    const display = () => {
      switch(route) {
      case "nav": return 
        <Navigation />;
      case "sign": return
        <Signin />
      }
    }

    return (
      <div className="App">
        <ParticlesBg type="cobweb" color="#ffffff" bg={{position: "fixed", left: 0, top: 0}} className="particles-bg"/>
        { display() }
      </div>
    )
  }

}

