import React, {Component} from 'react';
import './App.css';
import ParticlesBg from 'particles-bg';
import Logo from './Components/Logo/Logo';
import Navigation from './Components/Navigation/Navigation';
import Signin from './Components/Signin/Signin';
import Register from './Components/Register/Register';
import URLinput from './Components/URLinput/URLinput';
import Rank from './Components/Rank/Rank';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Clarifai from 'clarifai';
import 'tachyons';

const app = new Clarifai.App({
  apiKey: 'af3372ee16b04b788518c454dd35f8f9'
})

class App extends Component {
  constructor() {
    super();
     this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
  //     isSignedIn: false,
  //     user: {
  //       id: '',
  //       name: '',
  //       email: '',
  //       entries: 0,
  //       joined: ''
  //     }
     }
  }

  // // componentDidMount() {
  // //   fetch('http://localhost:3001/')
  // //     .then(response => response.json())
  // //     .then(console.log)
  // // }

  // loadUser = (data) => {
  //   this.setState({user: {
  //     id: data.id,
  //     name: data.name,
  //     email: data.email,
  //     entries: data.entries,
  //     joined: data.joined
  //   }})
  // }

//vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv

  calculateFaceLocation = (response) => {
    const clarifaiFace = response.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height),
    }
  }

  displayFaceBox = (box) => {
    this.setState({box: box});
  }

  onInputChange = (event) => {
    console.log('oninputchange', event.target.value); //this works
    // this.setState({input: event.target.value});
    // console.log('oninputchange', this.state)  //monitors URL input
  }

  onButtonSubmit = () => {
    console.log('click')
    this.setState({imageUrl: this.state.input});        //THIS DOES NOT REWRITE THE IMAGEURL STATE
    console.log('onbuttonsubmit', this.state.imageUrl)   //monitors final URL input
    // app.models
    //     .predict(
    //       {
    //         id: 'face-detection',
    //         name: 'face-detection',
    //         version: '6dc7e46bc9124c5c8824be4822abe105',
    //         type: 'visual-detector',
    //       }, this.state.input)
    //     // .then((response) => {
    //     //   if(response) {
    //     //     fetch('http://locahost:3001/image', 
    //     //       {
    //     //           method: 'put',
    //     //           headers: {'Content-Type': 'application/json'},
    //     //           body: JSON.stringify({
    //     //              id: this.state.user.id
    //     //           })
    //     //       })
    //         .then(response => response.json())   //this doesnt work
    //         .then(count => {
    //           this.setState(Object.assign(this.state.user, {entries: count}))
    //         // })
    //       // }
    //       this.displayFaceBox(this.calculateFaceLocation(response))
    //     })
    //     .catch(error => console.log(error))
  }

  //^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^

  onRouteChange = (route) => {
    console.log('func route', route)  
    this.setState({route: route})
  }


  render() {
    let componentToRender;
    console.log('act route', this.state.route)
    switch(this.state.route) {
    case 'home': componentToRender = (
        <div>
          <Logo />
          <Rank />
          <URLinput onInputChange={this.onInputChange} />
          {/*<FaceRecognition />*/}
        </div>
      );
      break;
    case 'signin': componentToRender = (
        <Signin onRouteChange={this.onRouteChange} />
      )
      break;
    case 'register': componentToRender = (
        <Register onRouteChange={this.onRouteChange} />
      )
      break;
    default: componentToRender = (
        <div>404</div>
      )
    }

    return (
      <div className="App">
        <ParticlesBg type="cobweb" color="#ffffff" bg={{position: "fixed", left: 0, top: 0}} className="particles-bg"/>
        <Navigation onRouteChange={this.onRouteChange} />
        { componentToRender }
      </div>
    );
  }





}



//const IMAGE_URL = 'https://samples.clarifai.com/face-det.jpg'


export default App;
